import { HttpParams } from '@angular/common/http';
export class AddHttpParams {
  constructor(resource) {
    this.resource = resource;
  }

  createParams() {
    const paramsValue = {};

    if (!this.resource) {
      return new HttpParams();
    }

    for (const prop of Object.keys(this.resource)) {
      const value = this.resource[prop];

      if (value || typeof value === 'number' && Number(value) === Number(0) || typeof value === 'boolean') {
        paramsValue[`${prop}`] = String(value);
      }
    }

    return new HttpParams({
      fromObject: Object.assign({}, paramsValue)
    });
  }

}
